mapper.select:
select
FROM custom_beloneto_ent cbe LEFT JOIN custom_info ci ON ci.id = cbe.custom_id LEFT JOIN ly_organization lo ON cbe.organization_id = lo.id where cbe.isdelete = 0 AND ci.isvalid = 1
AND cbe.organization_id = ${organization_id}
AND cbe.insert_time >= ${insert_time_start}
AND cbe.insert_time <= '${insert_time_end}'
AND ci.name = '${name}'
ORDER BY ${orderby}

SELECT cbe.organization_id, cbe.insert_time, cbe.id as belongToId, (SELECT per.check_time FROM physical_examination_record per WHERE per.custom_id = ci.id AND per.organization_id = cbe.organization_id ORDER BY per.check_time DESC LIMIT 1) AS last_check_time, ci.id, ci.idcard, ci.mobile, ci.name, ci.sex, ci.birthday, ci.body_height, ci.weight FROM custom_beloneto_ent cbe LEFT JOIN custom_info ci ON cbe.custom_id = ci.id WHERE ci.isvalid = 1 AND cbe.isdelete = 0
AND ci.name = '${name}'
AND ci.idcard = '${idcard}'
AND ci.mobile = '${mobile}'
AND cbe.organization_id = ${organization_id}
ORDER BY ${orderby}
